schema:
{
 "description": "Trail is the Schema for the Trails API",
 "properties": {
  "spec": {
   "description": "TrailSpec defines the desired state of Trail.\n\n\nThe settings for a trail.",
   "properties": {
    "cloudWatchLogsLogGroupARN": {
     "description": "Specifies a log group name using an Amazon Resource Name (ARN), a unique\nidentifier that represents the log group to which CloudTrail logs will be\ndelivered. Not required unless you specify CloudWatchLogsRoleArn.",
     "type": "string"
    },
    "cloudWatchLogsRoleARN": {
     "description": "Specifies the role for the CloudWatch Logs endpoint to assume to write to\na user's log group.",
     "type": "string"
    },
    "enableLogFileValidation": {
     "description": "Specifies whether log file integrity validation is enabled. The default is\nfalse.\n\n\nWhen you disable log file integrity validation, the chain of digest files\nis broken after one hour. CloudTrail does not create digest files for log\nfiles that were delivered during a period in which log file integrity validation\nwas disabled. For example, if you enable log file integrity validation at\nnoon on January 1, disable it at noon on January 2, and re-enable it at noon\non January 10, digest files will not be created for the log files delivered\nfrom noon on January 2 to noon on January 10. The same applies whenever you\nstop CloudTrail logging or delete a trail.",
     "type": "boolean"
    },
    "includeGlobalServiceEvents": {
     "description": "Specifies whether the trail is publishing events from global services such\nas IAM to the log files.",
     "type": "boolean"
    },
    "isMultiRegionTrail": {
     "description": "Specifies whether the trail is created in the current region or in all regions.\nThe default is false, which creates a trail only in the region where you\nare signed in. As a best practice, consider creating trails that log events\nin all regions.",
     "type": "boolean"
    },
    "isOrganizationTrail": {
     "description": "Specifies whether the trail is created for all accounts in an organization\nin Organizations, or only for the current Amazon Web Services account. The\ndefault is false, and cannot be true unless the call is made on behalf of\nan Amazon Web Services account that is the management account for an organization\nin Organizations.",
     "type": "boolean"
    },
    "kmsKeyID": {
     "description": "Specifies the KMS key ID to use to encrypt the logs delivered by CloudTrail.\nThe value can be an alias name prefixed by \"alias/\", a fully specified ARN\nto an alias, a fully specified ARN to a key, or a globally unique identifier.\n\n\nCloudTrail also supports KMS multi-Region keys. For more information about\nmulti-Region keys, see Using multi-Region keys (https://docs.aws.amazon.com/kms/latest/developerguide/multi-region-keys-overview.html)\nin the Key Management Service Developer Guide.\n\n\nExamples:\n\n\n  - alias/MyAliasName\n\n\n  - arn:aws:kms:us-east-2:123456789012:alias/MyAliasName\n\n\n  - arn:aws:kms:us-east-2:123456789012:key/12345678-1234-1234-1234-123456789012\n\n\n  - 12345678-1234-1234-1234-123456789012",
     "type": "string"
    },
    "name": {
     "description": "Specifies the name of the trail. The name must meet the following requirements:\n\n\n  - Contain only ASCII letters (a-z, A-Z), numbers (0-9), periods (.), underscores\n    (_), or dashes (-)\n\n\n  - Start with a letter or number, and end with a letter or number\n\n\n  - Be between 3 and 128 characters\n\n\n  - Have no adjacent periods, underscores or dashes. Names like my-_namespace\n    and my--namespace are not valid.\n\n\n  - Not be in IP address format (for example, 192.168.5.4)",
     "type": "string"
    },
    "s3BucketName": {
     "description": "Specifies the name of the Amazon S3 bucket designated for publishing log\nfiles. See Amazon S3 Bucket Naming Requirements (https://docs.aws.amazon.com/awscloudtrail/latest/userguide/create_trail_naming_policy.html).",
     "type": "string"
    },
    "s3KeyPrefix": {
     "description": "Specifies the Amazon S3 key prefix that comes after the name of the bucket\nyou have designated for log file delivery. For more information, see Finding\nYour CloudTrail Log Files (https://docs.aws.amazon.com/awscloudtrail/latest/userguide/cloudtrail-find-log-files.html).\nThe maximum length is 200 characters.",
     "type": "string"
    },
    "snsTopicName": {
     "description": "Specifies the name of the Amazon SNS topic defined for notification of log\nfile delivery. The maximum length is 256 characters.",
     "type": "string"
    },
    "tags": {
     "items": {
      "description": "A custom key-value pair associated with a resource such as a CloudTrail trail.",
      "properties": {
       "key": {
        "type": "string"
       },
       "value": {
        "type": "string"
       }
      },
      "type": "object"
     },
     "type": "array"
    }
   },
   "required": [
    "name",
    "s3BucketName"
   ],
   "type": "object"
  }
 },
 "title": "Trail",
 "type": "object"
}
