schema:
{
 "description": "Endpoint is the Schema for the Endpoints API",
 "properties": {
  "spec": {
   "description": "EndpointSpec defines the desired state of Endpoint.\n\n\nA global endpoint used to improve your application's availability by making\nit regional-fault tolerant. For more information about global endpoints,\nsee Making applications Regional-fault tolerant with global endpoints and\nevent replication (https://docs.aws.amazon.com/eventbridge/latest/userguide/eb-global-endpoints.html)\nin the Amazon EventBridge User Guide.",
   "properties": {
    "description": {
     "description": "A description of the global endpoint.",
     "type": "string"
    },
    "eventBuses": {
     "description": "Define the event buses used.\n\n\nThe names of the event buses must be identical in each Region.",
     "items": {
      "description": "The event buses the endpoint is associated with.",
      "properties": {
       "eventBusARN": {
        "type": "string"
       }
      },
      "type": "object"
     },
     "type": "array"
    },
    "name": {
     "description": "The name of the global endpoint. For example, \"Name\":\"us-east-2-custom_bus_A-endpoint\".",
     "type": "string"
    },
    "replicationConfig": {
     "description": "Enable or disable event replication. The default state is ENABLED which means\nyou must supply a RoleArn. If you don't have a RoleArn or you don't want\nevent replication enabled, set the state to DISABLED.",
     "properties": {
      "state": {
       "type": "string"
      }
     },
     "type": "object"
    },
    "roleARN": {
     "description": "The ARN of the role used for replication.",
     "type": "string"
    },
    "routingConfig": {
     "description": "Configure the routing policy, including the health check and secondary Region..",
     "properties": {
      "failoverConfig": {
       "description": "The failover configuration for an endpoint. This includes what triggers failover\nand what happens when it's triggered.",
       "properties": {
        "primary": {
         "description": "The primary Region of the endpoint.",
         "properties": {
          "healthCheck": {
           "type": "string"
          }
         },
         "type": "object"
        },
        "secondary": {
         "description": "The secondary Region that processes events when failover is triggered or\nreplication is enabled.",
         "properties": {
          "route": {
           "type": "string"
          }
         },
         "type": "object"
        }
       },
       "type": "object"
      }
     },
     "type": "object"
    }
   },
   "required": [
    "eventBuses",
    "name",
    "routingConfig"
   ],
   "type": "object"
  }
 },
 "title": "Endpoint",
 "type": "object"
}
