schema:
{
 "description": "ResolverEndpoint is the Schema for the ResolverEndpoints API",
 "properties": {
  "spec": {
   "description": "ResolverEndpointSpec defines the desired state of ResolverEndpoint.\n\n\nIn the response to a CreateResolverEndpoint (https://docs.aws.amazon.com/Route53/latest/APIReference/API_route53resolver_CreateResolverEndpoint.html),\nDeleteResolverEndpoint (https://docs.aws.amazon.com/Route53/latest/APIReference/API_route53resolver_DeleteResolverEndpoint.html),\nGetResolverEndpoint (https://docs.aws.amazon.com/Route53/latest/APIReference/API_route53resolver_GetResolverEndpoint.html),\nUpdates the name, or ResolverEndpointType for an endpoint, or UpdateResolverEndpoint\n(https://docs.aws.amazon.com/Route53/latest/APIReference/API_route53resolver_UpdateResolverEndpoint.html)\nrequest, a complex type that contains settings for an existing inbound or\noutbound Resolver endpoint.",
   "properties": {
    "direction": {
     "description": "Specify the applicable value:\n\n\n  - INBOUND: Resolver forwards DNS queries to the DNS service for a VPC\n    from your network\n\n\n  - OUTBOUND: Resolver forwards DNS queries from the DNS service for a VPC\n    to your network",
     "type": "string"
    },
    "ipAddresses": {
     "description": "The subnets and IP addresses in your VPC that DNS queries originate from\n(for outbound endpoints) or that you forward DNS queries to (for inbound\nendpoints). The subnet ID uniquely identifies a VPC.",
     "items": {
      "description": "In a CreateResolverEndpoint (https://docs.aws.amazon.com/Route53/latest/APIReference/API_route53resolver_CreateResolverEndpoint.html)\nrequest, the IP address that DNS queries originate from (for outbound endpoints)\nor that you forward DNS queries to (for inbound endpoints). IpAddressRequest\nalso includes the ID of the subnet that contains the IP address.",
      "properties": {
       "ip": {
        "type": "string"
       },
       "ipv6": {
        "type": "string"
       },
       "subnetID": {
        "type": "string"
       },
       "subnetRef": {
        "description": "Reference field for SubnetID",
        "properties": {
         "from": {
          "description": "AWSResourceReference provides all the values necessary to reference another\nk8s resource for finding the identifier(Id/ARN/Name)",
          "properties": {
           "name": {
            "type": "string"
           }
          },
          "type": "object"
         }
        },
        "type": "object"
       }
      },
      "type": "object"
     },
     "type": "array"
    },
    "name": {
     "description": "A friendly name that lets you easily find a configuration in the Resolver\ndashboard in the Route 53 console.",
     "type": "string"
    },
    "resolverEndpointType": {
     "description": "For the endpoint type you can choose either IPv4, IPv6. or dual-stack. A\ndual-stack endpoint means that it will resolve via both IPv4 and IPv6. This\nendpoint type is applied to all IP addresses.",
     "type": "string"
    },
    "securityGroupIDs": {
     "description": "The ID of one or more security groups that you want to use to control access\nto this VPC. The security group that you specify must include one or more\ninbound rules (for inbound Resolver endpoints) or outbound rules (for outbound\nResolver endpoints). Inbound and outbound rules must allow TCP and UDP access.\nFor inbound access, open port 53. For outbound access, open the port that\nyou're using for DNS queries on your network.",
     "items": {
      "type": "string"
     },
     "type": "array"
    },
    "securityGroupRefs": {
     "items": {
      "description": "AWSResourceReferenceWrapper provides a wrapper around *AWSResourceReference\ntype to provide more user friendly syntax for references using 'from' field\nEx:\nAPIIDRef:\n\n\n\tfrom:\n\t  name: my-api",
      "properties": {
       "from": {
        "description": "AWSResourceReference provides all the values necessary to reference another\nk8s resource for finding the identifier(Id/ARN/Name)",
        "properties": {
         "name": {
          "type": "string"
         }
        },
        "type": "object"
       }
      },
      "type": "object"
     },
     "type": "array"
    },
    "tags": {
     "description": "A list of the tag keys and values that you want to associate with the endpoint.",
     "items": {
      "description": "One tag that you want to add to the specified resource. A tag consists of\na Key (a name for the tag) and a Value.",
      "properties": {
       "key": {
        "type": "string"
       },
       "value": {
        "type": "string"
       }
      },
      "type": "object"
     },
     "type": "array"
    }
   },
   "required": [
    "direction",
    "ipAddresses"
   ],
   "type": "object"
  }
 },
 "title": "Resolver Endpoint",
 "type": "object"
}
