schema:
{
 "description": "Domain is the Schema for the Domains API",
 "properties": {
  "spec": {
   "description": "DomainSpec defines the desired state of Domain.",
   "properties": {
    "appNetworkAccessType": {
     "description": "Specifies the VPC used for non-EFS traffic. The default value is PublicInternetOnly.\n\n\n  - PublicInternetOnly - Non-EFS traffic is through a VPC managed by Amazon\n    SageMaker, which allows direct internet access\n\n\n  - VpcOnly - All Studio traffic is through the specified VPC and subnets",
     "type": "string"
    },
    "appSecurityGroupManagement": {
     "description": "The entity that creates and manages the required security groups for inter-app\ncommunication in VPCOnly mode. Required when CreateDomain.AppNetworkAccessType\nis VPCOnly and DomainSettings.RStudioServerProDomainSettings.DomainExecutionRoleArn\nis provided.",
     "type": "string"
    },
    "authMode": {
     "description": "The mode of authentication that members use to access the domain.",
     "type": "string"
    },
    "defaultUserSettings": {
     "description": "The default settings to use to create a user profile when UserSettings isn't\nspecified in the call to the CreateUserProfile API.\n\n\nSecurityGroups is aggregated when specified in both calls. For all other\nsettings in UserSettings, the values specified in CreateUserProfile take\nprecedence over those specified in CreateDomain.",
     "properties": {
      "executionRole": {
       "type": "string"
      },
      "jupyterServerAppSettings": {
       "description": "The JupyterServer app settings.",
       "properties": {
        "defaultResourceSpec": {
         "description": "Specifies the ARN's of a SageMaker image and SageMaker image version, and\nthe instance type that the version runs on.",
         "properties": {
          "instanceType": {
           "type": "string"
          },
          "lifecycleConfigARN": {
           "type": "string"
          },
          "sageMakerImageARN": {
           "type": "string"
          },
          "sageMakerImageVersionARN": {
           "type": "string"
          }
         },
         "type": "object"
        },
        "lifecycleConfigARNs": {
         "items": {
          "type": "string"
         },
         "type": "array"
        }
       },
       "type": "object"
      },
      "kernelGatewayAppSettings": {
       "description": "The KernelGateway app settings.",
       "properties": {
        "customImages": {
         "items": {
          "description": "A custom SageMaker image. For more information, see Bring your own SageMaker\nimage (https://docs.aws.amazon.com/sagemaker/latest/dg/studio-byoi.html).",
          "properties": {
           "appImageConfigName": {
            "type": "string"
           },
           "imageName": {
            "type": "string"
           },
           "imageVersionNumber": {
            "format": "int64",
            "type": "integer"
           }
          },
          "type": "object"
         },
         "type": "array"
        },
        "defaultResourceSpec": {
         "description": "Specifies the ARN's of a SageMaker image and SageMaker image version, and\nthe instance type that the version runs on.",
         "properties": {
          "instanceType": {
           "type": "string"
          },
          "lifecycleConfigARN": {
           "type": "string"
          },
          "sageMakerImageARN": {
           "type": "string"
          },
          "sageMakerImageVersionARN": {
           "type": "string"
          }
         },
         "type": "object"
        },
        "lifecycleConfigARNs": {
         "items": {
          "type": "string"
         },
         "type": "array"
        }
       },
       "type": "object"
      },
      "rStudioServerProAppSettings": {
       "description": "A collection of settings that configure user interaction with the RStudioServerPro\napp. RStudioServerProAppSettings cannot be updated. The RStudioServerPro\napp must be deleted and a new one created to make any changes.",
       "properties": {
        "accessStatus": {
         "type": "string"
        },
        "userGroup": {
         "type": "string"
        }
       },
       "type": "object"
      },
      "securityGroups": {
       "items": {
        "type": "string"
       },
       "type": "array"
      },
      "sharingSettings": {
       "description": "Specifies options for sharing SageMaker Studio notebooks. These settings\nare specified as part of DefaultUserSettings when the CreateDomain API is\ncalled, and as part of UserSettings when the CreateUserProfile API is called.\nWhen SharingSettings is not specified, notebook sharing isn't allowed.",
       "properties": {
        "notebookOutputOption": {
         "type": "string"
        },
        "s3KMSKeyID": {
         "type": "string"
        },
        "s3OutputPath": {
         "type": "string"
        }
       },
       "type": "object"
      },
      "tensorBoardAppSettings": {
       "description": "The TensorBoard app settings.",
       "properties": {
        "defaultResourceSpec": {
         "description": "Specifies the ARN's of a SageMaker image and SageMaker image version, and\nthe instance type that the version runs on.",
         "properties": {
          "instanceType": {
           "type": "string"
          },
          "lifecycleConfigARN": {
           "type": "string"
          },
          "sageMakerImageARN": {
           "type": "string"
          },
          "sageMakerImageVersionARN": {
           "type": "string"
          }
         },
         "type": "object"
        }
       },
       "type": "object"
      }
     },
     "type": "object"
    },
    "domainName": {
     "description": "A name for the domain.",
     "type": "string"
    },
    "domainSettings": {
     "description": "A collection of Domain settings.",
     "properties": {
      "rStudioServerProDomainSettings": {
       "description": "A collection of settings that configure the RStudioServerPro Domain-level\napp.",
       "properties": {
        "defaultResourceSpec": {
         "description": "Specifies the ARN's of a SageMaker image and SageMaker image version, and\nthe instance type that the version runs on.",
         "properties": {
          "instanceType": {
           "type": "string"
          },
          "lifecycleConfigARN": {
           "type": "string"
          },
          "sageMakerImageARN": {
           "type": "string"
          },
          "sageMakerImageVersionARN": {
           "type": "string"
          }
         },
         "type": "object"
        },
        "domainExecutionRoleARN": {
         "type": "string"
        },
        "rStudioConnectURL": {
         "type": "string"
        },
        "rStudioPackageManagerURL": {
         "type": "string"
        }
       },
       "type": "object"
      },
      "securityGroupIDs": {
       "items": {
        "type": "string"
       },
       "type": "array"
      }
     },
     "type": "object"
    },
    "homeEFSFileSystemKMSKeyID": {
     "description": "Use KmsKeyId.",
     "type": "string"
    },
    "kmsKeyID": {
     "description": "SageMaker uses Amazon Web Services KMS to encrypt the EFS volume attached\nto the domain with an Amazon Web Services managed key by default. For more\ncontrol, specify a customer managed key.",
     "type": "string"
    },
    "subnetIDs": {
     "description": "The VPC subnets that Studio uses for communication.",
     "items": {
      "type": "string"
     },
     "type": "array"
    },
    "tags": {
     "description": "Tags to associated with the Domain. Each tag consists of a key and an optional\nvalue. Tag keys must be unique per resource. Tags are searchable using the\nSearch API.\n\n\nTags that you specify for the Domain are also added to all Apps that the\nDomain launches.",
     "items": {
      "description": "A tag object that consists of a key and an optional value, used to manage\nmetadata for SageMaker Amazon Web Services resources.\n\n\nYou can add tags to notebook instances, training jobs, hyperparameter tuning\njobs, batch transform jobs, models, labeling jobs, work teams, endpoint configurations,\nand endpoints. For more information on adding tags to SageMaker resources,\nsee AddTags.\n\n\nFor more information on adding metadata to your Amazon Web Services resources\nwith tagging, see Tagging Amazon Web Services resources (https://docs.aws.amazon.com/general/latest/gr/aws_tagging.html).\nFor advice on best practices for managing Amazon Web Services resources with\ntagging, see Tagging Best Practices: Implement an Effective Amazon Web Services\nResource Tagging Strategy (https://d1.awsstatic.com/whitepapers/aws-tagging-best-practices.pdf).",
      "properties": {
       "key": {
        "type": "string"
       },
       "value": {
        "type": "string"
       }
      },
      "type": "object"
     },
     "type": "array"
    },
    "vpcID": {
     "description": "The ID of the Amazon Virtual Private Cloud (VPC) that Studio uses for communication.",
     "type": "string"
    }
   },
   "required": [
    "authMode",
    "defaultUserSettings",
    "domainName",
    "subnetIDs",
    "vpcID"
   ],
   "type": "object"
  }
 },
 "title": "Domain",
 "type": "object"
}
