schema:
{
 "description": "Endpoint is the Schema for the Endpoints API",
 "properties": {
  "spec": {
   "description": "EndpointSpec defines the desired state of Endpoint.\n\n\nA hosted endpoint for real-time inference.",
   "properties": {
    "deploymentConfig": {
     "description": "The deployment configuration for an endpoint, which contains the desired\ndeployment strategy and rollback configurations.",
     "properties": {
      "autoRollbackConfiguration": {
       "description": "Automatic rollback configuration for handling endpoint deployment failures\nand recovery.",
       "properties": {
        "alarms": {
         "items": {
          "description": "An Amazon CloudWatch alarm configured to monitor metrics on an endpoint.",
          "properties": {
           "alarmName": {
            "type": "string"
           }
          },
          "type": "object"
         },
         "type": "array"
        }
       },
       "type": "object"
      },
      "blueGreenUpdatePolicy": {
       "description": "Update policy for a blue/green deployment. If this update policy is specified,\nSageMaker creates a new fleet during the deployment while maintaining the\nold fleet. SageMaker flips traffic to the new fleet according to the specified\ntraffic routing configuration. Only one update policy should be used in the\ndeployment configuration. If no update policy is specified, SageMaker uses\na blue/green deployment strategy with all at once traffic shifting by default.",
       "properties": {
        "maximumExecutionTimeoutInSeconds": {
         "format": "int64",
         "type": "integer"
        },
        "terminationWaitInSeconds": {
         "format": "int64",
         "type": "integer"
        },
        "trafficRoutingConfiguration": {
         "description": "Defines the traffic routing strategy during an endpoint deployment to shift\ntraffic from the old fleet to the new fleet.",
         "properties": {
          "canarySize": {
           "description": "Specifies the endpoint capacity to activate for production.",
           "properties": {
            "type_": {
             "type": "string"
            },
            "value": {
             "format": "int64",
             "type": "integer"
            }
           },
           "type": "object"
          },
          "linearStepSize": {
           "description": "Specifies the endpoint capacity to activate for production.",
           "properties": {
            "type_": {
             "type": "string"
            },
            "value": {
             "format": "int64",
             "type": "integer"
            }
           },
           "type": "object"
          },
          "type_": {
           "type": "string"
          },
          "waitIntervalInSeconds": {
           "format": "int64",
           "type": "integer"
          }
         },
         "type": "object"
        }
       },
       "type": "object"
      }
     },
     "type": "object"
    },
    "endpointConfigName": {
     "description": "The name of an endpoint configuration. For more information, see CreateEndpointConfig.",
     "type": "string"
    },
    "endpointName": {
     "description": "The name of the endpoint.The name must be unique within an Amazon Web Services\nRegion in your Amazon Web Services account. The name is case-insensitive\nin CreateEndpoint, but the case is preserved and must be matched in .",
     "type": "string"
    },
    "tags": {
     "description": "An array of key-value pairs. You can use tags to categorize your Amazon Web\nServices resources in different ways, for example, by purpose, owner, or\nenvironment. For more information, see Tagging Amazon Web Services Resources\n(https://docs.aws.amazon.com/general/latest/gr/aws_tagging.html).",
     "items": {
      "description": "A tag object that consists of a key and an optional value, used to manage\nmetadata for SageMaker Amazon Web Services resources.\n\n\nYou can add tags to notebook instances, training jobs, hyperparameter tuning\njobs, batch transform jobs, models, labeling jobs, work teams, endpoint configurations,\nand endpoints. For more information on adding tags to SageMaker resources,\nsee AddTags.\n\n\nFor more information on adding metadata to your Amazon Web Services resources\nwith tagging, see Tagging Amazon Web Services resources (https://docs.aws.amazon.com/general/latest/gr/aws_tagging.html).\nFor advice on best practices for managing Amazon Web Services resources with\ntagging, see Tagging Best Practices: Implement an Effective Amazon Web Services\nResource Tagging Strategy (https://d1.awsstatic.com/whitepapers/aws-tagging-best-practices.pdf).",
      "properties": {
       "key": {
        "type": "string"
       },
       "value": {
        "type": "string"
       }
      },
      "type": "object"
     },
     "type": "array"
    }
   },
   "required": [
    "endpointConfigName",
    "endpointName"
   ],
   "type": "object"
  }
 },
 "title": "Endpoint",
 "type": "object"
}
