schema:
{
 "description": "ModelExplainabilityJobDefinition is the Schema for the ModelExplainabilityJobDefinitions API",
 "properties": {
  "spec": {
   "description": "ModelExplainabilityJobDefinitionSpec defines the desired state of ModelExplainabilityJobDefinition.",
   "properties": {
    "jobDefinitionName": {
     "description": "The name of the model explainability job definition. The name must be unique\nwithin an Amazon Web Services Region in the Amazon Web Services account.",
     "type": "string"
    },
    "jobResources": {
     "description": "Identifies the resources to deploy for a monitoring job.",
     "properties": {
      "clusterConfig": {
       "description": "Configuration for the cluster used to run model monitoring jobs.",
       "properties": {
        "instanceCount": {
         "format": "int64",
         "type": "integer"
        },
        "instanceType": {
         "type": "string"
        },
        "volumeKMSKeyID": {
         "type": "string"
        },
        "volumeSizeInGB": {
         "format": "int64",
         "type": "integer"
        }
       },
       "type": "object"
      }
     },
     "type": "object"
    },
    "modelExplainabilityAppSpecification": {
     "description": "Configures the model explainability job to run a specified Docker container\nimage.",
     "properties": {
      "configURI": {
       "type": "string"
      },
      "environment": {
       "additionalProperties": {
        "type": "string"
       },
       "type": "object"
      },
      "imageURI": {
       "type": "string"
      }
     },
     "type": "object"
    },
    "modelExplainabilityBaselineConfig": {
     "description": "The baseline configuration for a model explainability job.",
     "properties": {
      "baseliningJobName": {
       "type": "string"
      },
      "constraintsResource": {
       "description": "The constraints resource for a monitoring job.",
       "properties": {
        "s3URI": {
         "type": "string"
        }
       },
       "type": "object"
      }
     },
     "type": "object"
    },
    "modelExplainabilityJobInput": {
     "description": "Inputs for the model explainability job.",
     "properties": {
      "endpointInput": {
       "description": "Input object for the endpoint",
       "properties": {
        "endTimeOffset": {
         "type": "string"
        },
        "endpointName": {
         "type": "string"
        },
        "featuresAttribute": {
         "type": "string"
        },
        "inferenceAttribute": {
         "type": "string"
        },
        "localPath": {
         "type": "string"
        },
        "probabilityAttribute": {
         "type": "string"
        },
        "probabilityThresholdAttribute": {
         "type": "number"
        },
        "s3DataDistributionType": {
         "type": "string"
        },
        "s3InputMode": {
         "type": "string"
        },
        "startTimeOffset": {
         "type": "string"
        }
       },
       "type": "object"
      }
     },
     "type": "object"
    },
    "modelExplainabilityJobOutputConfig": {
     "description": "The output configuration for monitoring jobs.",
     "properties": {
      "kmsKeyID": {
       "type": "string"
      },
      "monitoringOutputs": {
       "items": {
        "description": "The output object for a monitoring job.",
        "properties": {
         "s3Output": {
          "description": "Information about where and how you want to store the results of a monitoring\njob.",
          "properties": {
           "localPath": {
            "type": "string"
           },
           "s3URI": {
            "type": "string"
           },
           "s3UploadMode": {
            "type": "string"
           }
          },
          "type": "object"
         }
        },
        "type": "object"
       },
       "type": "array"
      }
     },
     "type": "object"
    },
    "networkConfig": {
     "description": "Networking options for a model explainability job.",
     "properties": {
      "enableInterContainerTrafficEncryption": {
       "type": "boolean"
      },
      "enableNetworkIsolation": {
       "type": "boolean"
      },
      "vpcConfig": {
       "description": "Specifies a VPC that your training jobs and hosted models have access to.\nControl access to and from your training and model containers by configuring\nthe VPC. For more information, see Protect Endpoints by Using an Amazon Virtual\nPrivate Cloud (https://docs.aws.amazon.com/sagemaker/latest/dg/host-vpc.html)\nand Protect Training Jobs by Using an Amazon Virtual Private Cloud (https://docs.aws.amazon.com/sagemaker/latest/dg/train-vpc.html).",
       "properties": {
        "securityGroupIDs": {
         "items": {
          "type": "string"
         },
         "type": "array"
        },
        "subnets": {
         "items": {
          "type": "string"
         },
         "type": "array"
        }
       },
       "type": "object"
      }
     },
     "type": "object"
    },
    "roleARN": {
     "description": "The Amazon Resource Name (ARN) of an IAM role that Amazon SageMaker can assume\nto perform tasks on your behalf.",
     "type": "string"
    },
    "stoppingCondition": {
     "description": "A time limit for how long the monitoring job is allowed to run before stopping.",
     "properties": {
      "maxRuntimeInSeconds": {
       "format": "int64",
       "type": "integer"
      }
     },
     "type": "object"
    },
    "tags": {
     "description": "(Optional) An array of key-value pairs. For more information, see Using Cost\nAllocation Tags (https://docs.aws.amazon.com/awsaccountbilling/latest/aboutv2/cost-alloc-tags.html#allocation-whatURL)\nin the Amazon Web Services Billing and Cost Management User Guide.",
     "items": {
      "description": "A tag object that consists of a key and an optional value, used to manage\nmetadata for SageMaker Amazon Web Services resources.\n\n\nYou can add tags to notebook instances, training jobs, hyperparameter tuning\njobs, batch transform jobs, models, labeling jobs, work teams, endpoint configurations,\nand endpoints. For more information on adding tags to SageMaker resources,\nsee AddTags.\n\n\nFor more information on adding metadata to your Amazon Web Services resources\nwith tagging, see Tagging Amazon Web Services resources (https://docs.aws.amazon.com/general/latest/gr/aws_tagging.html).\nFor advice on best practices for managing Amazon Web Services resources with\ntagging, see Tagging Best Practices: Implement an Effective Amazon Web Services\nResource Tagging Strategy (https://d1.awsstatic.com/whitepapers/aws-tagging-best-practices.pdf).",
      "properties": {
       "key": {
        "type": "string"
       },
       "value": {
        "type": "string"
       }
      },
      "type": "object"
     },
     "type": "array"
    }
   },
   "required": [
    "jobDefinitionName",
    "jobResources",
    "modelExplainabilityAppSpecification",
    "modelExplainabilityJobInput",
    "modelExplainabilityJobOutputConfig",
    "roleARN"
   ],
   "type": "object"
  }
 },
 "title": "Model Explainability Job Definition",
 "type": "object"
}
