schema:
{
 "description": "StateMachine is the Schema for the StateMachines API",
 "properties": {
  "spec": {
   "description": "StateMachineSpec defines the desired state of StateMachine.",
   "properties": {
    "definition": {
     "description": "The Amazon States Language definition of the state machine. See Amazon States\nLanguage (https://docs.aws.amazon.com/step-functions/latest/dg/concepts-amazon-states-language.html).",
     "type": "string"
    },
    "loggingConfiguration": {
     "description": "Defines what execution history events are logged and where they are logged.\n\n\nBy default, the level is set to OFF. For more information see Log Levels\n(https://docs.aws.amazon.com/step-functions/latest/dg/cloudwatch-log-level.html)\nin the AWS Step Functions User Guide.",
     "properties": {
      "destinations": {
       "items": {
        "properties": {
         "cloudWatchLogsLogGroup": {
          "properties": {
           "logGroupARN": {
            "type": "string"
           }
          },
          "type": "object"
         }
        },
        "type": "object"
       },
       "type": "array"
      },
      "includeExecutionData": {
       "type": "boolean"
      },
      "level": {
       "type": "string"
      }
     },
     "type": "object"
    },
    "name": {
     "description": "The name of the state machine.\n\n\nA name must not contain:\n\n\n  - white space\n\n\n  - brackets \u003c \u003e { } [ ]\n\n\n  - wildcard characters ? *\n\n\n  - special characters \" # % \\ ^ | ~ ` $ \u0026 , ; : /\n\n\n  - control characters (U+0000-001F, U+007F-009F)\n\n\nTo enable logging with CloudWatch Logs, the name should only contain 0-9,\nA-Z, a-z, - and _.",
     "type": "string"
    },
    "roleARN": {
     "description": "The Amazon Resource Name (ARN) of the IAM role to use for this state machine.",
     "type": "string"
    },
    "tags": {
     "description": "Tags to be added when creating a state machine.\n\n\nAn array of key-value pairs. For more information, see Using Cost Allocation\nTags (https://docs.aws.amazon.com/awsaccountbilling/latest/aboutv2/cost-alloc-tags.html)\nin the AWS Billing and Cost Management User Guide, and Controlling Access\nUsing IAM Tags (https://docs.aws.amazon.com/IAM/latest/UserGuide/access_iam-tags.html).\n\n\nTags may only contain Unicode letters, digits, white space, or these symbols:\n_ . : / = + - @.",
     "items": {
      "description": "Tags are key-value pairs that can be associated with Step Functions state\nmachines and activities.\n\n\nAn array of key-value pairs. For more information, see Using Cost Allocation\nTags (https://docs.aws.amazon.com/awsaccountbilling/latest/aboutv2/cost-alloc-tags.html)\nin the AWS Billing and Cost Management User Guide, and Controlling Access\nUsing IAM Tags (https://docs.aws.amazon.com/IAM/latest/UserGuide/access_iam-tags.html).\n\n\nTags may only contain Unicode letters, digits, white space, or these symbols:\n_ . : / = + - @.",
      "properties": {
       "key": {
        "type": "string"
       },
       "value": {
        "type": "string"
       }
      },
      "type": "object"
     },
     "type": "array"
    },
    "tracingConfiguration": {
     "description": "Selects whether AWS X-Ray tracing is enabled.",
     "properties": {
      "enabled": {
       "type": "boolean"
      }
     },
     "type": "object"
    },
    "type_": {
     "description": "Determines whether a Standard or Express state machine is created. The default\nis STANDARD. You cannot update the type of a state machine once it has been\ncreated.",
     "type": "string"
    }
   },
   "required": [
    "definition",
    "name",
    "roleARN"
   ],
   "type": "object"
  }
 },
 "title": "State Machine",
 "type": "object"
}
