schema:
{
 "description": "Topic is the Schema for the Topics API",
 "properties": {
  "spec": {
   "description": "TopicSpec defines the desired state of Topic.\n\n\nA wrapper type for the topic's Amazon Resource Name (ARN). To retrieve a\ntopic's attributes, use GetTopicAttributes.",
   "properties": {
    "contentBasedDeduplication": {
     "type": "string"
    },
    "dataProtectionPolicy": {
     "description": "The body of the policy document you want to use for this topic.\n\n\nYou can only add one policy per topic.\n\n\nThe policy must be in JSON string format.\n\n\nLength Constraints: Maximum length of 30,720.",
     "type": "string"
    },
    "deliveryPolicy": {
     "type": "string"
    },
    "displayName": {
     "type": "string"
    },
    "fifoTopic": {
     "type": "string"
    },
    "kmsMasterKeyID": {
     "type": "string"
    },
    "kmsMasterKeyRef": {
     "description": "AWSResourceReferenceWrapper provides a wrapper around *AWSResourceReference\ntype to provide more user friendly syntax for references using 'from' field\nEx:\nAPIIDRef:\n\n\n\tfrom:\n\t  name: my-api",
     "properties": {
      "from": {
       "description": "AWSResourceReference provides all the values necessary to reference another\nk8s resource for finding the identifier(Id/ARN/Name)",
       "properties": {
        "name": {
         "type": "string"
        }
       },
       "type": "object"
      }
     },
     "type": "object"
    },
    "name": {
     "description": "The name of the topic you want to create.\n\n\nConstraints: Topic names must be made up of only uppercase and lowercase\nASCII letters, numbers, underscores, and hyphens, and must be between 1 and\n256 characters long.\n\n\nFor a FIFO (first-in-first-out) topic, the name must end with the .fifo suffix.",
     "type": "string"
    },
    "policy": {
     "type": "string"
    },
    "policyRef": {
     "description": "AWSResourceReferenceWrapper provides a wrapper around *AWSResourceReference\ntype to provide more user friendly syntax for references using 'from' field\nEx:\nAPIIDRef:\n\n\n\tfrom:\n\t  name: my-api",
     "properties": {
      "from": {
       "description": "AWSResourceReference provides all the values necessary to reference another\nk8s resource for finding the identifier(Id/ARN/Name)",
       "properties": {
        "name": {
         "type": "string"
        }
       },
       "type": "object"
      }
     },
     "type": "object"
    },
    "signatureVersion": {
     "type": "string"
    },
    "tags": {
     "description": "The list of tags to add to a new topic.\n\n\nTo be able to tag a topic on creation, you must have the sns:CreateTopic\nand sns:TagResource permissions.",
     "items": {
      "description": "The list of tags to be added to the specified topic.",
      "properties": {
       "key": {
        "type": "string"
       },
       "value": {
        "type": "string"
       }
      },
      "type": "object"
     },
     "type": "array"
    },
    "tracingConfig": {
     "type": "string"
    }
   },
   "required": [
    "name"
   ],
   "type": "object"
  }
 },
 "title": "Topic",
 "type": "object"
}
