schema:
{
 "properties": {
  "spec": {
   "description": "More info: https://www.vaultproject.io/docs/concepts/policies.html",
   "properties": {
    "policy": {
     "description": "Policy specifies a vault policy in json format.",
     "format": "textarea",
     "type": "string",
     "x-kubernetes-embedded-resource": true
    },
    "policyDocument": {
     "description": "PolicyDocument specifies a vault policy in hcl format. For example: path \"secret/*\" { capabilities = [\"create\", \"read\", \"update\", \"delete\", \"list\"] }",
     "type": "string"
    },
    "vaultPolicyName": {
     "description": "VaultPolicyName is the policy name set inside Vault. This defaults to following format: k8s.${cluster}.${metadata.namespace}.${metadata.name}",
     "type": "string"
    },
    "vaultRef": {
     "description": "VaultRef is the name of a AppBinding referencing to a Vault Server",
     "properties": {
      "name": {
       "description": "Name of the referent. More info: https://kubernetes.io/docs/concepts/overview/working-with-objects/names/#names TODO: Add other useful fields. apiVersion, kind, uid?",
       "type": "string"
      }
     },
     "type": "object",
     "x-kubernetes-map-type": "atomic"
    }
   },
   "required": [
    "vaultRef"
   ],
   "type": "object"
  }
 },
 "title": "Vault Policy",
 "type": "object"
}
