schema:
{
 "description": "PulsarConnection is the Schema for the pulsarconnections API",
 "properties": {
  "spec": {
   "description": "PulsarConnectionSpec defines the desired state of PulsarConnection",
   "properties": {
    "adminServiceSecureURL": {
     "description": "AdminServiceSecureURL is the admin service url for secure connection.",
     "pattern": "^https://.+$",
     "type": "string"
    },
    "adminServiceURL": {
     "description": "AdminServiceURL is the admin service url of the pulsar cluster",
     "pattern": "^https?://.+$",
     "type": "string"
    },
    "authentication": {
     "description": "Authentication defines authentication configurations",
     "properties": {
      "oauth2": {
       "description": "PulsarAuthenticationOAuth2 indicates the parameters which are need by pulsar OAuth2",
       "properties": {
        "audience": {
         "type": "string"
        },
        "clientID": {
         "type": "string"
        },
        "issuerEndpoint": {
         "type": "string"
        },
        "key": {
         "description": "ValueOrSecretRef is a string or a secret reference of the authentication",
         "properties": {
          "secretRef": {
           "description": "SecretKeyRef indicates a secret name and key",
           "properties": {
            "key": {
             "type": "string"
            },
            "name": {
             "type": "string"
            }
           },
           "required": [
            "key",
            "name"
           ],
           "type": "object"
          },
          "value": {
           "type": "string"
          }
         },
         "type": "object"
        },
        "scope": {
         "type": "string"
        }
       },
       "required": [
        "audience",
        "clientID",
        "issuerEndpoint",
        "key"
       ],
       "type": "object"
      },
      "token": {
       "description": "ValueOrSecretRef is a string or a secret reference of the authentication",
       "properties": {
        "secretRef": {
         "description": "SecretKeyRef indicates a secret name and key",
         "properties": {
          "key": {
           "type": "string"
          },
          "name": {
           "type": "string"
          }
         },
         "required": [
          "key",
          "name"
         ],
         "type": "object"
        },
        "value": {
         "type": "string"
        }
       },
       "type": "object"
      }
     },
     "type": "object"
    },
    "brokerClientTrustCertsFilePath": {
     "description": "BrokerClientTrustCertsFilePath Path for the trusted TLS certificate file for outgoing connection to a server (broker)",
     "type": "string"
    },
    "brokerServiceSecureURL": {
     "description": "BrokerServiceSecureURL is the broker service url for secure connection.",
     "pattern": "^pulsar\\+ssl://.+$",
     "type": "string"
    },
    "brokerServiceURL": {
     "description": "BrokerServiceURL is the broker service url of the pulsar cluster",
     "pattern": "^pulsar?://.+$",
     "type": "string"
    },
    "clusterName": {
     "description": "ClusterName indicates the local cluster name of the pulsar cluster. It should set when enabling the Geo Replication",
     "type": "string"
    }
   },
   "type": "object"
  }
 },
 "title": "Pulsar Connection",
 "type": "object"
}
