schema:
{
 "description": "PulsarNamespace is the Schema for the pulsarnamespaces API",
 "properties": {
  "spec": {
   "description": "PulsarNamespaceSpec defines the desired state of PulsarNamespace",
   "properties": {
    "backlogQuotaLimitSize": {
     "anyOf": [
      {
       "type": "integer"
      },
      {
       "type": "string"
      }
     ],
     "pattern": "^(\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))(([KMGTPE]i)|[numkMGTPE]|([eE](\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))))?$",
     "x-kubernetes-int-or-string": true
    },
    "backlogQuotaLimitTime": {
     "description": "Backlog Should set at least one of them if setting backlog",
     "type": "string"
    },
    "backlogQuotaRetentionPolicy": {
     "type": "string"
    },
    "backlogQuotaType": {
     "description": "BacklogQuotaType controls the backlog by setting the type to destination_storage or message_age destination_storage limits backlog by size (in bytes). message_age limits backlog by time, that is, message timestamp (broker or publish timestamp)",
     "enum": [
      "destination_storage",
      "message_age"
     ],
     "type": "string"
    },
    "bundles": {
     "format": "int32",
     "type": "integer"
    },
    "connectionRef": {
     "description": "ConnectionRef is the reference to the PulsarConnection resource",
     "properties": {
      "name": {
       "description": "Name of the referent. More info: https://kubernetes.io/docs/concepts/overview/working-with-objects/names/#names TODO: Add other useful fields. apiVersion, kind, uid?",
       "type": "string"
      }
     },
     "type": "object"
    },
    "geoReplicationRefs": {
     "description": "GeoReplicationRefs is the reference list to the PulsarGeoReplication resource",
     "items": {
      "description": "LocalObjectReference contains enough information to let you locate the referenced object inside the same namespace.",
      "properties": {
       "name": {
        "description": "Name of the referent. More info: https://kubernetes.io/docs/concepts/overview/working-with-objects/names/#names TODO: Add other useful fields. apiVersion, kind, uid?",
        "type": "string"
       }
      },
      "type": "object"
     },
     "type": "array"
    },
    "lifecyclePolicy": {
     "description": "PulsarResourceLifeCyclePolicy indicates whether it will keep or delete the resource in pulsar cluster after resource is deleted by controller KeepAfterDeletion or CleanUpAfterDeletion",
     "enum": [
      "CleanUpAfterDeletion",
      "KeepAfterDeletion"
     ],
     "type": "string"
    },
    "maxConsumersPerSubscription": {
     "format": "int32",
     "type": "integer"
    },
    "maxConsumersPerTopic": {
     "format": "int32",
     "type": "integer"
    },
    "maxProducersPerTopic": {
     "description": "Tenant Policy Setting",
     "format": "int32",
     "type": "integer"
    },
    "messageTTL": {
     "description": "MessageTTL indicates the message ttl for the namespace",
     "type": "string"
    },
    "name": {
     "description": "Name is the namespace name",
     "type": "string"
    },
    "retentionSize": {
     "anyOf": [
      {
       "type": "integer"
      },
      {
       "type": "string"
      }
     ],
     "pattern": "^(\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))(([KMGTPE]i)|[numkMGTPE]|([eE](\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))))?$",
     "x-kubernetes-int-or-string": true
    },
    "retentionTime": {
     "description": "Retention Should set at least one of them if setting retention Retention Quota must exceed configured backlog quota for namespace",
     "type": "string"
    }
   },
   "required": [
    "connectionRef",
    "name"
   ],
   "type": "object"
  }
 },
 "title": "Pulsar Namespace",
 "type": "object"
}
