schema:
{
 "description": "PulsarPermission is the Schema for the pulsarpermissions API",
 "properties": {
  "spec": {
   "description": "PulsarPermissionSpec defines the desired state of PulsarPermission",
   "properties": {
    "actions": {
     "description": "Actions contains a list of action to grant. the options include produce,consume,functions",
     "items": {
      "type": "string"
     },
     "type": "array"
    },
    "connectionRef": {
     "description": "ConnectionRef is the reference to the PulsarConnection resource",
     "properties": {
      "name": {
       "description": "Name of the referent. More info: https://kubernetes.io/docs/concepts/overview/working-with-objects/names/#names TODO: Add other useful fields. apiVersion, kind, uid?",
       "type": "string"
      }
     },
     "type": "object"
    },
    "lifecyclePolicy": {
     "description": "LifecyclePolicy is the policy that how to deal with pulsar resource when PulsarPermission is deleted",
     "type": "string"
    },
    "resourceName": {
     "description": "ResourceName name of the target resource which will be granted the permssions",
     "type": "string"
    },
    "resourceType": {
     "description": "ResourceType indicates the resource type, the options include namespace and topic",
     "enum": [
      "namespace",
      "topic"
     ],
     "type": "string"
    },
    "roles": {
     "description": "Roles contains a list of role which will be granted the same permissions for the same target",
     "items": {
      "type": "string"
     },
     "type": "array"
    }
   },
   "required": [
    "connectionRef",
    "resourceName",
    "resourceType",
    "roles"
   ],
   "type": "object"
  }
 },
 "title": "Pulsar Permission",
 "type": "object"
}
